SELECT c.nome, s.codsaidas AS codvenda, s.data, e.precocusto AS precocustounit, e.precocomdesconto AS precounit, e.precooriginal-e.precocomdesconto AS desconto, e.quantidade, e.precocomdesconto*e.quantidade AS precototal, (e.precocomdesconto*e.quantidade)-(e.precocusto*e.quantidade) AS lucro, fp.descricao AS formapgto FROM entsai AS e INNER JOIN saidas AS s ON (s.codsaidas=e.codentsai) INNER JOIN clientes AS c ON (s.codcli=c.codigo) INNER JOIN movpagamentos AS m ON (m.codorigem=s.codsaidas)  INNER JOIN formapgto AS fp ON (fp.codigoformapgto=m.codformapgto) WHERE e.codmerc=".inputdialog('Entre com o codigo da mercadoria:')." AND e.tipo='S' AND s.data>='".inputdialog('Entre com a data inicial (AAAA-MM-DD)')."' AND s.data<='".inputdialog('Entre com a data final (AAAA-MM-DD)')."'