SELECT c.codfor, f.nome, c.data, e.quantidade, e.precooriginal  FROM entsai AS e INNER JOIN entradas AS c ON e.codentsai=c.codentradas INNER JOIN fornecedores AS f ON c.codfor=f.codigo WHERE e.tipo='E' AND e.codmerc=".inputdialog('Cod. da Mercadoria:')." AND c.data>='".inputdialog('Entre com a data inicial (AAAA-MM-DD)')."' AND c.data<='".inputdialog('Entre com a data final (AAAA-MM-DD)')."'